  cur = con.cursor()
  cur.execute("SELECT * FROM NOTIFICATION")
  rows = cur.fetchall()
  for row in rows:
    data.append({"notification_id":row[0],"logo":row[1],"company_name":row[2],"tag_line":row[3],"isDelete":row[4],"isViewed":row[5]})
