def insert_note_data(data):
    con= sql.connect("internhub.db")
    query = "INSERT INTO NOTIFICATION( logo, company_name, tag_line,isDelete,isViewed) VALUES(?,?,?,?,?)"
    con.execute(query,(data["logo"],data["company_name"],data["tag_line"],"0","0"))
    con.commit()
    con.close()

